import React, { useEffect } from 'react';

const SocureComponent = () => {
  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://websdk.socure.com/bundle.js';
    script.type = 'text/javascript';
    document.head.appendChild(script);

    let sdkInitiated = false;
    let changesDone = false;

    const onProgress = (progress) => {
      console.log(progress);
      console.log('progress');
    };

    const onSuccess = (response) => {
      console.log(response);
      console.log('success');
    };

    const onError = (error) => {
      console.log(error);
      console.log('error');
    };

    const config = {
      onProgress: onProgress,
      onSuccess: onSuccess,
      onError: onError,
      qrCodeNeeded: true,
    };

    const inputData = {
      customerUserId: 'customIDFromTheCustomer',
    };

    const inpconfig = {
      sendMessage: true,
      dataMandatory: false,
    };

    const processConfig = {
      language: 'en',
      documentType: 'license',
    };

    const clearSession = () => {
      window.Socure.cleanup();
      window.Socure.reset();
      sessionStorage.removeItem('documentVerificationToken');
      sessionStorage.removeItem('publicApiKey');
      localStorage.removeItem('devicer_id');
      console.log('Socure DocV Session cleaned!');
    };

    const start = () => {
      if (sdkInitiated) {
        clearSession();
        console.log('cleaned');
        sdkInitiated = false;
      }

      window.SocureInitializer.init('SDK_KEY').then((lib) => {
        lib.init('SDK_KEY', '#websdk', config).then(() => {
          lib.start(1, inputData, processConfig).then(
            (response) => {
              console.log(response);
              sdkInitiated = true;
            },
            (error) => {
              console.log(error);
            }
          );
        });
      });
    };

    window.start = start;
  }, []);

  const start = () => {
    window.start();
  };

  return (
    <>
      <button onClick={start}>Start</button>
      <div id="websdk"></div>
    </>
  );
};

export default SocureComponent;
