import React, { useEffect, useImperativeHandle, forwardRef, useState } from 'react';

const SocureComponent = forwardRef(({
  step = 1,
  inputData = {},
  processConfig = {},
  sdkKey,
  onSuccessCallback,
  onErrorCallback,
}, ref) => {

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://websdk.socure.com/bundle.js';
    script.type = 'text/javascript';
    script.onload = () => {
      setScriptLoaded(true);
    };
    document.head.appendChild(script);
  }, []);

  let sdkInitiated = false;
  let changesDone = false;

  const [scriptLoaded, setScriptLoaded] = useState(false);

  const onProgress = (progress) => {
    console.log(progress);
    console.log('progress');
  };

  const onSuccess = (response) => {
    console.log(response);
    console.log('success');
    if (typeof onSuccessCallback === 'function') {
      onSuccessCallback(response);
    }
  };

  const onError = (error) => {
    console.log(error);
    console.log('error');
    if (typeof onErrorCallback === 'function') {
      onErrorCallback(error);
    }
  };

  const config = {
    onProgress: onProgress,
    onSuccess: onSuccess,
    onError: onError,
    qrCodeNeeded: true,
  };

  const clearSession = () => {
    window.Socure.cleanup();
    window.Socure.reset();
    sessionStorage.removeItem('documentVerificationToken');
    sessionStorage.removeItem('publicApiKey');
    localStorage.removeItem('devicer_id');
    console.log('Socure DocV Session cleaned!');
  };

  const start = () => {
    if (!scriptLoaded) {
      console.error('Socure script not yet loaded');
      return;
    }

    if (sdkInitiated) {
      clearSession();
      console.log('cleaned');
      sdkInitiated = false;
    }

    if (!sdkKey) {
      console.error('SDK key is required');
      return;
    }

    window.SocureInitializer.init(sdkKey).then((lib) => {
      lib.init(sdkKey, '#websdk', config).then(() => {
        lib.start(step, inputData, processConfig).then(
          (response) => {
            console.log(response);
            sdkInitiated = true;
          },
          (error) => {
            console.log(error);
          }
        );
      });
    });
  };

  useImperativeHandle(ref, () => ({
    start,
  }));



  return (
    <>
      <div id="websdk"></div>
    </>
  );
});

export default SocureComponent;
